import React, {useState} from "react";

const FirewallForm = (props) =>{
    const[form, setForm] = useState("");

    const newDoc = (e) =>{
        e.preventDefault();
        const doc = {form};
        console.log("Original doc captured");
    };
    function stringSplitter(doc){
        var stringArr = doc.split(/(\d+)/);
        var newEntries=[];
        var newDoc="";
        for(var i=0; i<stringArr.length; i++){
            if(stringArr[i].includes("p")){
                stringArr.splice(i,1)
            }
            if(stringArr[i] ==" "){
                stringArr[i] = "-";
            }
        }
        for(var j=0; j<stringArr.length; j++){
            if(stringArr[j+1]=="-"){
                newEntries.push(`edit "tcp_`+stringArr[j]+stringArr[j+1]+stringArr[j+2]+ `"\
                set tcp-portrange `+ stringArr[j]+stringArr[j+1]+stringArr[j+2] +`\
                next`);
            }else{
                newEntries.push(`edit "tcp_`+ stringArr[j]+`"\
                set tcp-portrange `+ stringArr[j]+`\
                next`);
            }
        }
        for(var k=0; k<newEntries.length; k++){
            newDoc+= newEntries[k];
        }
        return newDoc
    }
    return(
        <div>
        <h1>Format for conversion: One entry per line, no commas. Ex. below. </h1>
            <p>port-object eq 7000
                port-object eq 5280
                port-object eq 587
                port-object eq 10022
                port-object eq 10032
                port-object range 8080 8089`</p>
        <form onSubmit = {newDoc}>
            <div>
                <label> Original Entries here</label>
                <input type="submit" name="form" id="formSubmit" class= "btn btn-success"/>
                <textarea rows="300" cols="50" id="form"></textarea>
            </div>
        </form>

        </div>
    )
}
export default FirewallForm;
